import React from 'react';

import './Caption.css';

const CaptionLeft = {
  // right: {{props.right}}
}

const CaptionRight = {
  left: "12px"
}

const Caption = (props) => (
    <div className={props.side}>
    {props.right}
      <h1 className="captionHeader">{props.header}</h1>
      <h3 className="captionText">{props.text}</h3>
    </div>
);


export default Caption;
