import React from 'react';

import './Caption.css';

const CaptionLeft = {
  width: "300px",
  margin: "30px auto",
  backgroundColor: "#44014C",
  minHeight: "200px",
  boxSizing: "border-box"
}

const CaptionRight = {
  padding: "10px 20px",
  textAlign: "center",
  color: "white",
  fontSize: "22px"
}

const Caption = (props) => (
    <div className="caption" style={CaptionLeft}>
      <h1 className="captionHeader">{props.header}</h1>
      <h3 className="captionText">{props.text}</h3>
    </div>
);


export default Caption;
