import React, { Component } from 'react';
import Modal from 'react-modal';

import shoe from './images/sketch-shoe.png';
import furniture from './images/sketch-furniture.png';
import pet from './images/sketch-pet.png';
import wearable from './images/sketch-wearable.png';
import tent from './images/sketch-tent.png';
import mod from './images/sketch-mod.png';

import './Sketches.css';

const customStyles = {
  content : {
    top                   : '50%',
    left                  : '50%',
    right                 : 'auto',
    bottom                : 'auto',
    marginRight           : '-50%',
    transform             : 'translate(-50%, -50%)'
  }
};
// Modal.setAppElement('#root')
// Make sure to bind modal to your appElement (http://reactcommunity.org/react-modal/accessibility/)

class Sketches extends Component {
  constructor() {
    super();

    this.state = {
      modalIsOpen: false,
      modalImage: 0
    };

    this.openModal = this.openModal.bind(this);
    this.afterOpenModal = this.afterOpenModal.bind(this);
    this.closeModal = this.closeModal.bind(this);
  }

  openModal(imageIndex) {
    this.setState({modalIsOpen: true});
    switch (imageIndex) {
      case 0:
        this.modalImage = shoe;
        break;
      case 1:
        this.modalImage = furniture;
        break;
      case 2:
        this.modalImage = pet;
        break;
      case 3:
        this.modalImage = wearable;
        break;
      case 4:
        this.modalImage = tent;
        break;
      case 5:
        this.modalImage = mod;
    }
  }

  afterOpenModal() {
    // references are now sync'd and can be accessed.
    this.subtitle.style.color = '#eeeeee';
  }

  closeModal() {
    this.setState({modalIsOpen: false});
  }

  render() {
    return (
      <div id={this.props.id} className="Sketches main-section">
        <Modal
          isOpen={this.state.modalIsOpen}
          // onAfterOpen={this.afterOpenModal}
          onRequestClose={this.closeModal}
          style={customStyles}
          contentLabel="Example Modal">
          <img src={this.modalImage}/>
        </Modal>

        <header className="section-header">
          <p>...and a designer.</p>
        </header>
        <div className="Sketches-body">
          <div>
            <a onClick={() => this.openModal(0)}>
              <img src={shoe} className="Sketch-image" alt="shoe" />
            </a>
            <a onClick={this.openModal}><img src={furniture} className="Sketch-image" alt="furniture" /></a>
            <a onClick={this.openModal}><img src={pet} className="Sketch-image" alt="pet" /></a>
          </div>
          <div>
            <a onClick={this.openModal}><img src={wearable} className="Sketch-image" alt="wearable" /></a>
            <a onClick={this.openModal}><img src={tent} className="Sketch-image" alt="tent" /></a>
            <a onClick={this.openModal}><img src={mod} className="Sketch-image" alt="mod" /></a>
          </div>
        </div>
      </div>
    );
  }
}

export default Sketches;
