import React, { Component } from 'react';
import { Link } from "react-router-dom";
import NavBar from '../../components/Navbar/Navbar';
import DetailHeader from '../../components/DetailHeader/DetailHeader';
import Caption from '../../components/Caption/Caption';
import BackButton from '../../components/BackButton/BackButton';
import { StickyContainer, Sticky } from 'react-sticky';
import 'bootstrap/dist/css/bootstrap.css';
import { Bootstrap, Grid, Row, Col, Image } from 'react-bootstrap';

import SBHero from './images/sb_mockup.png';
import SBHome from './images/sb_home.PNG';
import SBClass from './images/sb_class.PNG';

import './StruggleBus.css';

const styles = {
    grid: {
        paddingLeft: 0,
        paddingRight: 0
    },
    col: {
        paddingLeft: 1,
        paddingRight: 1,
    }
};

class StruggleBus extends Component {
  render() {
    return (
      <div id={this.props.id} className="detail-page">
        <BackButton />

          <Grid fluid styles={styles.grid}>
            <Row className="detail-row">
              <DetailHeader name="StruggleBus" role="Developer" date="Apr - Jun 2018"
               tagline="An app that helps college students find friends to take classes with."/>
            </Row>

            <Row className="detail-row">
              <Col xs={6} style={styles.col}>
                <Caption side="caption-left" header="Student support system"
                 text=<p>Students are often wary of taking classes alone, and have no way of knowing what their friends
                 or acquaintances who they don't talk to regularly will take.<br/><br/>StruggleBus is a cross-platform
                 app built in Ionic that aides class registration for the Northwestern community. It allows students to
                 browse available courses and find Facebook friends interested in the same ones, encouraging them to form
                 a support system before a class even starts.</p>/>
              </Col>
               <Col xs={6} style={styles.col}>
                 <img className="detail-image-right" src={MGBonding}/>
               </Col>
            </Row>

            <Row className="detail-row">
               <Col xs={7} style={styles.col}>
                 <img className="detail-image-left" src={MGSession1}/>
               </Col>
               <Col xs={5} style={styles.col}>
                 <Caption side="caption-right" header="First sessions"
                  text=<p>We held our first two sessions in Spring 2018, reaching local girls and providing a fun
                  environment for them to learn about 3D printing and technology.</p>/>
               </Col>
            </Row>

            <Row className="detail-row">
              <Col xs={5} style={styles.col}>
                <Caption side="caption-left" header="Moving forward..."
                 text=<p>MakerGirl and the NU academy in particular continue to grow as we partner with different
                 groups such as CodeVerse and Girl Scouts, and enlist new team members. We now have a team of 16
                 at NU, and hold bimonthly sessions of up to 10 girls each.</p>/>
              </Col>
               <Col xs={7} style={styles.col}>
                 <img className="detail-image-right" src={MGSession2}/>
               </Col>

            </Row>
          </Grid>

      </div>
    );
  }
}

export default StruggleBus;
