import React from 'react';

import './Caption.css';

const sideToClassName = {
    left: "caption-left",
    right: "caption-right"
};

const Caption = (props) => (
    <div className="{sideToClassName[this.props.data.side]}">
    this.props.data.side
      <h1 className="captionHeader">{props.header}</h1>
      <h3 className="captionText">{props.text}</h3>
    </div>
);


export default Caption;
